package arrays;

public class Homework75 {
	// *Напишете програма, която създава масив с 10 елемента от
	// *тип double и инициализира всеки от елементите със стойност
	// *равна на индекса на елемента умножен по 3.
	// *Да се изведат елементите на екрана.
	static void executeHomework() {
	int size = 10;
	double[] array = new double[size];

	{

		for (int i = 0; i < 10; i++)

		{
			array[i] = i * 3;
			System.out.println(" " + array[i] + " " );
		}
		
	}
	}
}

